DROP TABLE `PERSON`;

CREATE TABLE `PERSON` (
  `id` int(8) unsigned NOT NULL auto_increment,
  `first_name` varchar(30) default NULL,
  `last_name` varchar(30) NOT NULL,
  `active_ind` varchar(1) NOT NULL,
  `activity_dt_tm` DATE NOT NULL,
  `created_dt_tm` DATE NOT NULL,
  PRIMARY KEY (`id`)
) AUTO_INCREMENT=1;

